import { useEffect, useState } from 'react';

const Clientes = () => {
  const [clientes, setClientes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchClientes = async (termo = '') => {
    setLoading(true);
    setError(null);
    try {
      let url = 'http://localhost:3001/api/v1/clientes/search';
      if (termo.trim() !== '') {
        url += `?termo=${encodeURIComponent(termo.trim())}`;
      }

      const res = await fetch(url);
      if (!res.ok) throw new Error('Erro ao buscar clientes');
      const data = await res.json();
      setClientes(Array.isArray(data) ? data : []);
    } catch (err) {
      setError(err.message);
      setClientes([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchClientes();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    fetchClientes(searchTerm);
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Lista de Clientes</h1>

      <form onSubmit={handleSearch} className="mb-4 flex gap-2">
        <input
          type="text"
          placeholder="Buscar por nome, email, cidade, etc..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="border border-gray-300 p-2 rounded w-full"
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
        >
          Buscar
        </button>
      </form>

      {loading && <p>Carregando...</p>}
      {error && <p className="text-red-500">{error}</p>}

      {!loading && clientes.length === 0 && <p>Nenhum cliente encontrado.</p>}

      <ul className="space-y-2">
        {clientes.map((cliente) => (
          <li
            key={cliente.id}
            className="border p-4 rounded shadow hover:shadow-md transition"
          >
            <p><strong>Nome:</strong> {cliente.Nome}</p>
            <p><strong>Email:</strong> {cliente.email}</p>
            <p><strong>Telefone:</strong> {cliente.telefone}</p>
            <p><strong>Cidade:</strong> {cliente.Cidade}</p>
            <p><strong>CPF/CNPJ:</strong> {cliente.CPF_CNPJ}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Clientes;
