import { yupResolver } from '@hookform/resolvers/yup';
import { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate, useParams } from 'react-router-dom';
import * as yup from 'yup';

const schema = yup.object().shape({
  Nome: yup.string().required('Nome é obrigatório'),
  CPF_CNPJ: yup.string().required('CPF/CNPJ é obrigatório'),
  CEP: yup.string().required('CEP é obrigatório'),
  Logradouro: yup.string().required('Logradouro é obrigatório'),
  Endereco: yup.string().required('Endereço é obrigatório'),
  Numero: yup.string().required('Número é obrigatório'),
  Bairro: yup.string().required('Bairro é obrigatório'),
  Cidade: yup.string().required('Cidade é obrigatório'),
  UF: yup.string().required('UF é obrigatório').length(2, 'UF deve ter 2 letras'),
  Complemento: yup.string(),
  telefone: yup.string().required('Telefone é obrigatório'),
  LimiteCredito: yup
    .number()
    .typeError('Limite Crédito deve ser número')
    .min(0, 'Limite Crédito não pode ser negativo')
    .required('Limite Crédito é obrigatório'),
  Validade: yup
    .date()
    .typeError('Validade deve ser uma data válida')
    .required('Validade é obrigatória'),
  email: yup.string().email('Email inválido').required('Email é obrigatório'),
});

const style = {
  container: {
    maxWidth: 600,
    margin: '40px auto',
    padding: 20,
    fontFamily: 'Arial, sans-serif',
    border: '1px solid #ddd',
    borderRadius: 8,
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
  },
  title: {
    textAlign: 'center',
    marginBottom: 20,
  },
  field: {
    marginBottom: 15,
  },
  label: {
    display: 'block',
    fontWeight: 'bold',
    marginBottom: 6,
  },
  input: {
    width: '100%',
    padding: 8,
    fontSize: 16,
    borderRadius: 4,
    border: '1px solid #ccc',
  },
  error: {
    color: 'red',
    marginTop: 4,
    fontSize: 14,
  },
  buttonsWrapper: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: 25,
  },
  button: {
    padding: '10px 20px',
    fontSize: 16,
    cursor: 'pointer',
    borderRadius: 5,
    border: 'none',
  },
  saveButton: {
    backgroundColor: '#28a745',
    color: '#fff',
  },
  cancelButton: {
    backgroundColor: '#dc3545',
    color: '#fff',
  },
};

export default function EditarCliente() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  useEffect(() => {
    async function fetchCliente() {
      try {
        const res = await fetch(`http://localhost:3001/api/v1/clientes/${id}`);
        if (!res.ok) throw new Error('Cliente não encontrado');
        const data = await res.json();

        // Formata a data e garante que telefone não seja undefined
        const validadeFormatada = data.Validade?.split('T')[0] || '';
        reset({
          ...data,
          Validade: validadeFormatada,
          telefone: data.telefone || '',
        });
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    }

    fetchCliente();
  }, [id, reset]);

  const onSubmit = async (formData) => {
    try {
      // Confirma que telefone está no formData
      const payload = { ...formData, telefone: formData.telefone };

      const res = await fetch(`http://localhost:3001/api/v1/clientes/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.error || 'Erro ao atualizar cliente');
      }

      alert('Cliente atualizado com sucesso!');
      navigate('/');
    } catch (err) {
      alert(err.message);
    }
  };

  if (loading) return <p style={{ textAlign: 'center' }}>Carregando cliente...</p>;
  if (error) return <p style={{ color: 'red', textAlign: 'center' }}>Erro: {error}</p>;

  return (
    <form onSubmit={handleSubmit(onSubmit)} style={style.container}>
      <h2 style={style.title}>Editar Cliente #{id}</h2>

      {[
        { label: 'Nome', name: 'Nome' },
        { label: 'CPF/CNPJ', name: 'CPF_CNPJ' },
        { label: 'CEP', name: 'CEP' },
        { label: 'Logradouro', name: 'Logradouro' },
        { label: 'Endereço', name: 'Endereco' },
        { label: 'Número', name: 'Numero' },
        { label: 'Bairro', name: 'Bairro' },
        { label: 'Cidade', name: 'Cidade' },
        { label: 'UF', name: 'UF', maxLength: 2 },
        { label: 'Complemento', name: 'Complemento' },
        { label: 'Telefone', name: 'telefone' },
        { label: 'Limite Crédito', name: 'LimiteCredito', type: 'number', step: '0.01' },
        { label: 'Validade', name: 'Validade', type: 'date' },
        { label: 'Email', name: 'email', type: 'email' },
      ].map(({ label, name, type, maxLength, step }) => (
        <div key={name} style={style.field}>
          <label htmlFor={name} style={style.label}>{label}:</label>
          <input
            id={name}
            type={type || 'text'}
            maxLength={maxLength}
            step={step}
            style={style.input}
            {...register(name)}
          />
          {errors[name] && <p style={style.error}>{errors[name].message}</p>}
        </div>
      ))}

      <div style={style.buttonsWrapper}>
        <button type="submit" style={{ ...style.button, ...style.saveButton }}>Salvar</button>
        <button
          type="button"
          onClick={() => navigate('/')}
          style={{ ...style.button, ...style.cancelButton }}
        >
          Cancelar
        </button>
      </div>
    </form>
  );
}
