import { yupResolver } from '@hookform/resolvers/yup';
import { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate, useParams } from 'react-router-dom';
import * as yup from 'yup';

const schema = yup.object().shape({
  Nome: yup.string().required('Nome é obrigatório'),
  CPF_CNPJ: yup.string().required('CPF/CNPJ é obrigatório'),
  CEP: yup.string().required('CEP é obrigatório'),
  Logradouro: yup.string().required('Logradouro é obrigatório'),
  Endereco: yup.string().required('Endereço é obrigatório'),
  Numero: yup.string().required('Número é obrigatório'),
  Bairro: yup.string().required('Bairro é obrigatório'),
  Cidade: yup.string().required('Cidade é obrigatório'),
  UF: yup.string().required('UF é obrigatório').length(2, 'UF deve ter 2 letras'),
  Complemento: yup.string(),
  telefone: yup.string().required('Telefone é obrigatório'),
  LimiteCredito: yup
    .number()
    .typeError('Limite Crédito deve ser número')
    .min(0, 'Limite Crédito não pode ser negativo')
    .required('Limite Crédito é obrigatório'),
  Validade: yup
    .date()
    .typeError('Validade deve ser uma data válida')
    .required('Validade é obrigatória'),
  email: yup.string().email('Email inválido').required('Email é obrigatório'),
});

export default function EditarCliente() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  useEffect(() => {
    async function fetchCliente() {
      try {
        const res = await fetch(`http://localhost:3001/api/v1/clientes/${id}`);
        if (!res.ok) throw new Error('Cliente não encontrado');
        const data = await res.json();

        const validadeFormatada = data.Validade?.split('T')[0] || '';
        reset({
          ...data,
          Validade: validadeFormatada,
          telefone: data.telefone || '',
        });
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    }

    fetchCliente();
  }, [id, reset]);

  const onSubmit = async (formData) => {
    try {
      const payload = { ...formData, telefone: formData.telefone };
      const res = await fetch(`http://localhost:3001/api/v1/clientes/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.error || 'Erro ao atualizar cliente');
      }

      alert('Cliente atualizado com sucesso!');
      navigate('/');
    } catch (err) {
      alert(err.message);
    }
  };

  if (loading) return <p className="text-center">Carregando cliente...</p>;
  if (error) return <p className="text-center text-red-500">Erro: {error}</p>;

  const campos = [
    { label: 'Nome', name: 'Nome' },
    { label: 'CPF/CNPJ', name: 'CPF_CNPJ' },
    { label: 'CEP', name: 'CEP' },
    { label: 'Logradouro', name: 'Logradouro' },
    { label: 'Endereço', name: 'Endereco' },
    { label: 'Número', name: 'Numero' },
    { label: 'Bairro', name: 'Bairro' },
    { label: 'Cidade', name: 'Cidade' },
    { label: 'UF', name: 'UF', maxLength: 2 },
    { label: 'Complemento', name: 'Complemento' },
    { label: 'Telefone', name: 'telefone' },
    { label: 'Limite Crédito', name: 'LimiteCredito', type: 'number', step: '0.01' },
    { label: 'Validade', name: 'Validade', type: 'date' },
    { label: 'Email', name: 'email', type: 'email' },
  ];

  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className="max-w-4xl mx-auto bg-white p-8 rounded-xl shadow-md"
    >
      <h2 className="text-2xl font-bold text-center mb-6">
        Editar Cliente #{id}
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {campos.map(({ label, name, type, maxLength, step }) => (
          <div key={name} className="flex flex-col">
            <label htmlFor={name} className="font-semibold mb-1">
              {label}:
            </label>
            <input
              id={name}
              type={type || 'text'}
              maxLength={maxLength}
              step={step}
              {...register(name)}
              className={`border border-gray-300 rounded-md px-3 py-2 ${
                errors[name] ? 'border-red-500' : ''
              }`}
            />
            {errors[name] && (
              <span className="text-red-500 text-sm mt-1">
                {errors[name].message}
              </span>
            )}
          </div>
        ))}
      </div>

      <div className="flex justify-between mt-8">
        <button
          type="submit"
          className="bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-6 rounded-md"
        >
          Salvar
        </button>
        <button
          type="button"
          onClick={() => navigate('/')}
          className="bg-red-600 hover:bg-red-700 text-white font-semibold py-2 px-6 rounded-md"
        >
          Cancelar
        </button>
      </div>
    </form>
  );
}
