import { Link } from 'react-router-dom';

export default function HomePage() {
  return (
    <div style={{
      maxWidth: '800px',
      margin: '40px auto',
      padding: '20px',
      fontFamily: 'Arial, sans-serif',
      textAlign: 'center',
      border: '1px solid #ddd',
      borderRadius: '8px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
    }}>
      <h1>Bem-vindo ao Sistema de Cadastro de Clientes</h1>
      
      <p style={{ fontSize: '18px', lineHeight: '1.5', margin: '20px 0' }}>
        Aqui você pode gerenciar os seus clientes de forma simples e rápida. 
        Cadastre novos clientes, edite informações importantes e mantenha tudo organizado.
      </p>
      
      <div style={{ marginTop: '30px' }}>
        <Link to="/clientes" style={buttonStyle}>Ver Lista de Clientes</Link>
        <Link to="/cadastro" style={{ ...buttonStyle, marginLeft: '15px' }}>Cadastrar Novo Cliente</Link>
      </div>
    </div>
  );
}

const buttonStyle = {
  display: 'inline-block',
  padding: '12px 24px',
  fontSize: '16px',
  textDecoration: 'none',
  color: '#fff',
  backgroundColor: '#007bff',
  borderRadius: '5px',
  cursor: 'pointer',
  transition: 'background-color 0.3s',
};
